namespace ARK\Actor;

use ARK\Model\Item;
use ARK\Model\ItemTrait;
use ARK\ORM\ORM;
use ARK\Security\ActorRole;
use Doctrine\Common\Collections\ArrayCollection;

class Actor implements Item
{
    use ItemTrait;

    protected $roles = null;

    public function __construct($schema = 'core.actor')
    {
        $this->construct($schema);
    }

    public function fullname()
    {
        $fullname = $this->property('fullname')->value();
        return ($fullname ? $fullname->content() : '');
    }

    public function roles()
    {
        if ($this->roles === null) {
            $ars = ORM::findBy(ActorRole::class, ['actor' => $this->id()]);
            $this->roles = new ArrayCollection();
            foreach ($ars as $ar) {
                $this->roles[] = $ar->role();
            }
        }
        return $this->roles;
    }
}
