    protected $roles = null;

    public function __construct($schema = 'core.actor')
    {
        $this->construct($schema);
    }

    public function fullname()
    {
        $fullname = $this->property('fullname')->value();
        return ($fullname ? $fullname->content() : '');
    }

    public function hasRole($role)
    {
        foreach ($this->roles() as $has) {
            if ($has == $role or $has->role()->id() == $role) {
                return true;
            }
        }
        return false;
    }

    public function roles()
    {
        if ($this->roles === null) {
            $this->roles = ORM::findBy(ActorRole::class, ['actor' => $this->id()]);
        }
        return $this->roles;
    }

    public function hasPermission($permission = null)
    {
        if ($permission === null) {
            return true;
        }
        if (is_string($permission)) {
            $permission = ORM::find(Permission::class, $permission);
        }
        if ($permission instanceof Permission) {
            foreach ($this->roles() as $role) {
                if ($role->role()->hasPermission($permission)) {
                    return true;
                }
            }
        }
        return false;
    }
}
